CREATE DATABASE my_netflix;
USE my_netflix;

DROP TABLE IF EXISTS my_viewing;

TRUNCATE TABLE my_viewing;

/* table creation*/

CREATE TABLE my_viewing
(
title varchar(200),
date date
);


/* creating new column */

ALTER TABLE my_viewing
ADD COLUMN season VARCHAR(100) after title;

ALTER TABLE my_viewing
ADD COLUMN series VARCHAR(100) after title;

ALTER TABLE my_viewing
ADD COLUMN episode VARCHAR(100) after season;

ALTER TABLE my_viewing
ADD COLUMN chapter VARCHAR(100) after episode;

ALTER TABLE my_viewing
CHANGE season season_series VARCHAR(100);

ALTER TABLE my_viewing
DROP COLUMN season_series;

/* updating column row data */

UPDATE my_viewing
SET series =
	CASE 
		WHEN INSTR(title, 'Series') > 0 
		THEN SUBSTR(title, INSTR(title, 'Series'))
		ELSE ""
	END;


UPDATE my_viewing
SET season =
	CASE 
		WHEN INSTR(title, 'Season') > 0 
		THEN SUBSTR(title, INSTR(title, 'Season'))
		ELSE ""
	END;


UPDATE my_viewing
SET episode =
	CASE 
		WHEN INSTR(title, 'Episode') > 0 
		THEN SUBSTR(title, INSTR(title, 'Episode'))
		ELSE ""
	END;

UPDATE my_viewing
SET chapter =
	CASE 
		WHEN INSTR(title, 'Chapter') > 0 
		THEN SUBSTR(title, INSTR(title, 'Chapter'))
		ELSE ""
	END;
    


