/* data viewing */

SELECT * FROM my_viewing;

/* Total show watched in netflix from year 2021-2023*/   
  
SELECT 
	COUNT(title) AS total_watched
FROM 
	my_viewing;

/* When did I start using netflix? */

SELECT 
	MIN(date) initial_usage
FROM 
	my_viewing;

/* What movies or series did I watched first? */

SELECT title
from my_viewing
where date = (SELECT MIN(date) start
				from my_viewing)
ORDER BY date;

/* Checking for null values in column genre */

SELECT *
FROM
	my_viewing
WHERE
	genre is NULL;

/* eliminating null values*/

DELETE FROM my_viewing
WHERE genre is NULL;

/* top 3 most watch genre*/

SELECT 
	COUNT(genre) total_genre, 
	genre
FROM
	my_viewing
GROUP BY 
	genre
ORDER BY total_genre desc
limit 3;

/* Wwhat are the titles of the top 3 most watched genres*/

SELECT title
FROM my_viewing
WHERE genre IN (SELECT 
					genre
				FROM
					my_viewing
				WHERE 
					genre IN ('ACTION','HORROR/SUSPENSE','ANIME'));
                    
/*Total number of My Hero Academia watched*/

SELECT
    COUNT(title) total_MHA_watched
FROM
	my_viewing
WHERE title like '%My Hero A%';

/* Extract only the series watched for MY HERO ACADEMIA excluding movie*/

SELECT MHA.title
FROM (SELECT
			title
		FROM
			my_viewing
		WHERE title like '%My Hero A%') MHA
WHERE MHA.title like '%season%';

/* my_viewing count distributed per year*/

SELECT 
	count(title) as NUMBER_WATCHED,
	2023 AS YEAR
FROM
	my_viewing
WHERE
	YEAR(date) = 2023
UNION
SELECT 
	count(title) as NUMBER_WATCHED,
	2022 AS YEAR
FROM
	my_viewing
WHERE
	YEAR(date) = 2022
UNION
SELECT 
	count(title) as NUMBER_WATCHED,
	2021 AS YEAR
FROM
	my_viewing
WHERE
	YEAR(date) = 2021;
    
/* Creating view for future use */
    
CREATE VIEW total_view_year as(
SELECT 
	count(title) as number_watched,
	2023 AS 'year'
FROM
	my_viewing
WHERE
	YEAR(date) = 2023
UNION
SELECT 
	count(title) as number_watched,
	2022 AS 'year'
FROM
	my_viewing
WHERE
	YEAR(date) = 2022
UNION
SELECT 
	count(title) as number_watched,
	2021 as 'year'
FROM
	my_viewing
WHERE
	YEAR(date) = 2021
);

DROP VIEW IF EXISTS total_view_year;

SELECT * FROM total_view_year;

/* ranking my year with the most number of view in netflix*/

SELECT RANK() OVER (ORDER BY number_watched DESC) AS RANKING_YEAR, NUMBER_WATCHED, year
FROM Total_view_year;

