SELECT CAST((RNK.number_watched/365) AS SIGNED) as avg_view_day
FROM (SELECT RANK() OVER (ORDER BY number_watched DESC) AS RANKING_YEAR, NUMBER_WATCHED, year
	FROM Total_view_year) RNK
WHERE RNK.RANKING_YEAR = 1;

